// Content generation and summarization request processor
const input = $input.first().json;

// Determine content operation type
const operation = input.operation || 'generate'; // 'generate', 'summarize', 'rewrite', 'translate'
const contentType = input.contentType || 'article'; // 'article', 'email', 'social', 'blog', 'report'
const tone = input.tone || 'professional'; // 'professional', 'casual', 'creative', 'technical'
const length = input.length || 'medium'; // 'short', 'medium', 'long'
const language = input.language || 'en';

// Content specifications
const contentSpecs = {
  article: {
    structure: 'Introduction, main points, conclusion',
    length: { short: '200-300 words', medium: '500-800 words', long: '1000-1500 words' },
    style: 'informative with clear headings'
  },
  email: {
    structure: 'Subject line, greeting, body, call-to-action, closing',
    length: { short: '50-100 words', medium: '150-250 words', long: '300-400 words' },
    style: 'clear and actionable'
  },
  social: {
    structure: 'Hook, value, call-to-action, hashtags',
    length: { short: '20-50 words', medium: '100-150 words', long: '200-280 words' },
    style: 'engaging and shareable'
  },
  blog: {
    structure: 'Title, intro, sections, conclusion, CTA',
    length: { short: '300-500 words', medium: '800-1200 words', long: '1500-2500 words' },
    style: 'conversational and valuable'
  },
  report: {
    structure: 'Executive summary, findings, recommendations',
    length: { short: '500-750 words', medium: '1000-1500 words', long: '2000-3000 words' },
    style: 'formal and data-driven'
  }
};

// Process input content for summarization/rewriting
let sourceContent = '';
if (operation === 'summarize' || operation === 'rewrite' || operation === 'translate') {
  sourceContent = input.content || input.text || 'No source content provided';
}

// Topic/prompt for generation
const topic = input.topic || input.prompt || 'AI and automation in business workflows';

// Additional parameters
const targetAudience = input.audience || 'general';
const keywords = input.keywords || [];
const includeImages = input.includeImages || false;

return [{
  json: {
    operation: operation,
    contentType: contentType,
    tone: tone,
    length: length,
    language: language,
    topic: topic,
    sourceContent: sourceContent,
    targetAudience: targetAudience,
    keywords: keywords,
    specifications: contentSpecs[contentType],
    timestamp: new Date().toISOString(),
    requestId: `content-${Date.now()}`
  }
}];

// AI prompt builder for different content operations
const request = $input.first().json;

let systemPrompt = '';
let userPrompt = '';
let temperature = 0.7;
let maxTokens = 1500;

switch(request.operation) {
  case 'generate':
    systemPrompt = `You are an expert ${request.contentType} writer. Create ${request.tone} content for ${request.targetAudience} audience. Structure: ${request.specifications.structure}. Style: ${request.specifications.style}. Target length: ${request.specifications.length[request.length]}.`;

    userPrompt = `Topic: ${request.topic}\n\n`;
    if (request.keywords.length > 0) {
      userPrompt += `Include these keywords: ${request.keywords.join(', ')}\n\n`;
    }
    userPrompt += `Please create a high-quality ${request.contentType} with a ${request.tone} tone.`;
    temperature = 0.8;
    maxTokens = request.length === 'long' ? 2500 : (request.length === 'medium' ? 1500 : 800);
    break;

  case 'summarize':
    systemPrompt = `You are an expert summarization specialist. Create concise, accurate summaries that capture key points, main arguments, and essential information. Maintain the original tone and perspective.`;

    userPrompt = `Please summarize this content in ${request.specifications.length[request.length]}:\n\n${request.sourceContent}`;
    temperature = 0.3;
    maxTokens = request.length === 'long' ? 1000 : (request.length === 'medium' ? 600 : 300);
    break;

  case 'rewrite':
    systemPrompt = `You are an expert content rewriter. Transform content while preserving meaning, improving clarity, and adapting tone to ${request.tone} style for ${request.targetAudience} audience.`;

    userPrompt = `Please rewrite this content with a ${request.tone} tone:\n\n${request.sourceContent}`;
    temperature = 0.6;
    maxTokens = Math.max(request.sourceContent.length * 1.5, 800);
    break;

  case 'translate':
    systemPrompt = `You are an expert translator. Provide accurate, natural translations that preserve meaning, tone, and cultural context. Adapt idioms and expressions appropriately.`;

    userPrompt = `Please translate this content to ${request.language}:\n\n${request.sourceContent}`;
    temperature = 0.2;
    maxTokens = Math.max(request.sourceContent.length * 1.2, 600);
    break;

  default:
    systemPrompt = 'You are a helpful content assistant.';
    userPrompt = request.topic;
}

return [{
  json: {
    ...request,
    aiConfig: {
      systemPrompt: systemPrompt,
      userPrompt: userPrompt,
      temperature: temperature,
      maxTokens: maxTokens,
      model: request.length === 'long' ? 'gpt-4' : 'gpt-3.5-turbo'
    }
  }
}];

// Content optimization and enhancement suggestions
const originalRequest = $('Content Request Processor').first().json;
const generatedContent = $('AI Content Generation').first().json;
const qualityAnalysis = $('Content Quality Analysis').first().json;

let content = 'Content generation failed';
let usage = {};

if (generatedContent && generatedContent.choices && generatedContent.choices[0]) {
  content = generatedContent.choices[0].message.content;
  usage = generatedContent.usage || {};
}

let qualityFeedback = 'Quality analysis unavailable';
let qualityScore = 0;

if (qualityAnalysis && qualityAnalysis.choices && qualityAnalysis.choices[0]) {
  qualityFeedback = qualityAnalysis.choices[0].message.content;

  // Try to extract numerical score
  const scoreMatch = qualityFeedback.match(/\b(\d+(\.\d+)?)\/10\b|\bscore.*?(\d+(\.\d+)?)\b/i);
  if (scoreMatch) {
    qualityScore = parseFloat(scoreMatch[1] || scoreMatch[3]);
  }
}

// Generate optimization suggestions based on content type and quality
let optimizations = [];
switch(originalRequest.contentType) {
  case 'email':
    optimizations = [
      'Add a compelling subject line if missing',
      'Include clear call-to-action buttons',
      'Personalize with recipient name/company',
      'Keep paragraphs short for mobile reading',
      'Test different subject lines for A/B testing'
    ];
    break;
  case 'social':
    optimizations = [
      'Add relevant hashtags for discoverability',
      'Include engaging visuals or emojis',
      'Ask questions to encourage engagement',
      'Time posting for optimal audience reach',
      'Include clear call-to-action'
    ];
    break;
  case 'blog':
    optimizations = [
      'Add SEO-optimized title and meta description',
      'Include internal/external links',
      'Break up text with subheadings and bullets',
      'Add relevant images with alt text',
      'Include author bio and social sharing buttons'
    ];
    break;
  case 'article':
    optimizations = [
      'Add compelling headline and subheadings',
      'Include data/statistics for credibility',
      'Add quotes or expert opinions',
      'Include actionable takeaways',
      'Optimize for readability score'
    ];
    break;
  default:
    optimizations = [
      'Review for clarity and conciseness',
      'Check grammar and spelling',
      'Ensure consistent tone throughout',
      'Add specific examples or case studies',
      'Include clear next steps or conclusions'
    ];
}

// Content metrics calculation
const wordCount = content.split(/\s+/).length;
const sentenceCount = content.split(/[.!?]+/).length;
const readabilityScore = Math.max(0, Math.min(100, 100 - (wordCount / sentenceCount * 2.5))); // Simplified readability
const avgWordsPerSentence = Math.round(wordCount / sentenceCount);

return [{
  json: {
    success: true,
    operation: originalRequest.operation,
    contentType: originalRequest.contentType,
    generatedContent: {
      content: content,
      wordCount: wordCount,
      sentenceCount: sentenceCount,
      avgWordsPerSentence: avgWordsPerSentence,
      estimatedReadingTime: Math.ceil(wordCount / 200)
    },
    qualityMetrics: {
      overallScore: qualityScore,
      readabilityScore: Math.round(readabilityScore),
      qualityFeedback: qualityFeedback
    },
    optimizations: optimizations,
    aiMetrics: {
      model: originalRequest.aiConfig?.model || 'gpt-3.5-turbo',
      tokensUsed: usage.total_tokens || 0,
      promptTokens: usage.prompt_tokens || 0,
      completionTokens: usage.completion_tokens || 0,
      estimatedCost: (usage.total_tokens || 0) * 0.00002
    },
    metadata: {
      requestId: originalRequest.requestId,
      processingTime: (Date.now() - new Date(originalRequest.timestamp).getTime()) / 1000,
      generatedAt: new Date().toISOString()
    }
  }
}];

// Format final output for display
const result = $input.first().json;

// Create formatted display
const displayOutput = {
  'ðŸš€ CONTENT GENERATION RESULTS': '=' .repeat(50),
  '': '',
  'ðŸ“‹ Request Details': {
    'Operation': result.operation.toUpperCase(),
    'Content Type': result.contentType.toUpperCase(),
    'Processing Time': `${result.metadata.processingTime} seconds`,
    'Request ID': result.metadata.requestId
  },
  ' ': '',
  'ðŸ“ Generated Content': {
    'Content': result.generatedContent.content,
    'Word Count': result.generatedContent.wordCount,
    'Reading Time': `${result.generatedContent.estimatedReadingTime} minutes`,
    'Avg Words/Sentence': result.generatedContent.avgWordsPerSentence
  },
  '  ': '',
  'â­ Quality Assessment': {
    'Overall Score': `${result.qualityMetrics.overallScore}/10`,
    'Readability Score': `${result.qualityMetrics.readabilityScore}/100`,
    'Quality Feedback': result.qualityMetrics.qualityFeedback
  },
  '   ': '',
  'ðŸ’¡ Optimization Suggestions': result.optimizations,
  '    ': '',
  'ðŸ¤– AI Performance': {
    'Model Used': result.aiMetrics.model,
    'Total Tokens': result.aiMetrics.tokensUsed,
    'Estimated Cost': `$${result.aiMetrics.estimatedCost.toFixed(4)}`,
    'Efficiency': result.aiMetrics.tokensUsed > 0 ? 
      `${Math.round(result.generatedContent.wordCount / result.aiMetrics.tokensUsed * 100)} words per 100 tokens` : 
      'N/A'
  }
};

// SEO and social media ready versions
let adaptedVersions = {};

if (result.contentType === 'blog' || result.contentType === 'article') {
  adaptedVersions.seoTitle = `${result.generatedContent.content.split('.')[0].substring(0, 60)}...`;
  adaptedVersions.metaDescription = `${result.generatedContent.content.substring(0, 155)}...`;
  adaptedVersions.socialPost = `${result.generatedContent.content.substring(0, 240)}... #AI #Content #Automation`;
}

if (result.contentType === 'email') {
  adaptedVersions.subjectLine = result.generatedContent.content.split('\n')[0];
  adaptedVersions.previewText = result.generatedContent.content.split('\n')[1]?.substring(0, 100);
}

return [{
  json: {
    displayOutput: displayOutput,
    rawResult: result,
    adaptedVersions: adaptedVersions,
    summary: {
      status: 'completed',
      operation: result.operation,
      contentType: result.contentType,
      wordCount: result.generatedContent.wordCount,
      qualityScore: result.qualityMetrics.overallScore,
      cost: result.aiMetrics.estimatedCost
    }
  }
}];
