// PDF and Document processing input handler
const input = $input.first().json;

// Document processing configuration
const documentUrl = input.documentUrl || input.pdfUrl || '';
const documentText = input.documentText || input.text || 'Sample contract document: This Software License Agreement ("Agreement") is entered into on January 15, 2024, between TechCorp Inc. ("Licensor") and BusinessSolutions Ltd. ("Licensee"). The license fee is $50,000 annually, renewable every 12 months. The software includes CRM modules, analytics dashboard, and API access. Support is provided 24/7 with 99.9% uptime guarantee. Termination clause allows either party to terminate with 30-day written notice. Confidentiality obligations extend 5 years post-termination.';
const processingType = input.processingType || 'comprehensive'; // 'comprehensive', 'extraction', 'summarization', 'compliance'
const documentType = input.documentType || 'contract'; // 'contract', 'financial', 'legal', 'technical', 'academic'
const language = input.language || 'en';

// Specialized processing prompts for different document types
const processingTemplates = {
  contract: {
    extraction: 'Extract key contractual elements: parties, dates, financial terms, obligations, termination clauses, and legal provisions.',
    compliance: 'Analyze for compliance risks, missing clauses, and legal vulnerabilities.',
    summarization: 'Create executive summary highlighting key terms, obligations, and risks.',
    comprehensive: 'Provide complete contract analysis including parties, terms, risks, recommendations, and compliance assessment.'
  },
  financial: {
    extraction: 'Extract financial data: revenue, expenses, profits, ratios, trends, and key performance indicators.',
    compliance: 'Check for financial compliance, audit requirements, and regulatory adherence.',
    summarization: 'Create financial performance summary with key metrics and trends.',
    comprehensive: 'Provide complete financial analysis including performance, risks, trends, and recommendations.'
  },
  legal: {
    extraction: 'Extract legal elements: statutes, precedents, arguments, conclusions, and legal reasoning.',
    compliance: 'Analyze legal compliance, potential violations, and regulatory requirements.',
    summarization: 'Summarize legal arguments, findings, and conclusions.',
    comprehensive: 'Provide complete legal analysis including arguments, precedents, risks, and recommendations.'
  },
  technical: {
    extraction: 'Extract technical specifications, requirements, architectures, and implementation details.',
    compliance: 'Check technical compliance with standards, best practices, and security requirements.',
    summarization: 'Summarize technical approach, architecture, and key implementation decisions.',
    comprehensive: 'Provide complete technical analysis including architecture, risks, and implementation recommendations.'
  },
  academic: {
    extraction: 'Extract research methodology, findings, conclusions, and key insights.',
    compliance: 'Check for research methodology compliance and academic standards.',
    summarization: 'Summarize research objectives, methodology, findings, and conclusions.',
    comprehensive: 'Provide complete research analysis including methodology, findings, limitations, and implications.'
  }
};

// Build processing context
const processingConfig = {
  documentUrl: documentUrl,
  documentText: documentText,
  processingType: processingType,
  documentType: documentType,
  language: language,
  template: processingTemplates[documentType] || processingTemplates.contract,
  prompt: processingTemplates[documentType][processingType] || processingTemplates[documentType].comprehensive,
  outputRequirements: {
    format: 'structured_json',
    sections: [
      'document_summary',
      'key_entities',
      'important_dates',
      'financial_terms',
      'obligations_and_rights',
      'risks_and_concerns',
      'recommendations',
      'compliance_assessment'
    ]
  },
  timestamp: new Date().toISOString(),
  requestId: `pdf-${Date.now()}`
};

return [{ json: processingConfig }];

// Process and integrate all analysis results
const config = $('Document Configuration Processor').first().json;
const geminiAnalysis = $('Gemini Document Analysis').first().json;
const geminiRisk = $('Gemini Risk Assessment').first().json;
const openaiAnalysis = $('OpenAI Comparison Analysis').first().json;

// Process Gemini document analysis
let geminiDocumentResult = {
  success: false,
  content: {},
  tokensUsed: 0,
  error: null
};

if (geminiAnalysis && geminiAnalysis.candidates && geminiAnalysis.candidates[0]) {
  try {
    const analysisText = geminiAnalysis.candidates[0].content.parts[0].text;
    geminiDocumentResult = {
      success: true,
      content: JSON.parse(analysisText),
      tokensUsed: geminiAnalysis.usageMetadata?.totalTokenCount || 0,
      model: geminiAnalysis.modelVersion || 'gemini-2.0-flash',
      error: null
    };
  } catch (error) {
    geminiDocumentResult.error = 'Failed to parse Gemini JSON response';
    geminiDocumentResult.rawContent = geminiAnalysis.candidates[0].content.parts[0].text;
  }
}

// Process Gemini risk assessment
let geminiRiskResult = {
  success: false,
  content: {},
  tokensUsed: 0,
  error: null
};

if (geminiRisk && geminiRisk.candidates && geminiRisk.candidates[0]) {
  try {
    const riskText = geminiRisk.candidates[0].content.parts[0].text;
    geminiRiskResult = {
      success: true,
      content: JSON.parse(riskText),
      tokensUsed: geminiRisk.usageMetadata?.totalTokenCount || 0,
      error: null
    };
  } catch (error) {
    geminiRiskResult.error = 'Failed to parse Gemini risk JSON';
    geminiRiskResult.rawContent = geminiRisk.candidates[0].content.parts[0].text;
  }
}

// Process OpenAI comparison
let openaiResult = {
  success: false,
  content: '',
  tokensUsed: 0,
  error: null
};

if (openaiAnalysis && openaiAnalysis.choices && openaiAnalysis.choices[0]) {
  openaiResult = {
    success: true,
    content: openaiAnalysis.choices[0].message.content,
    tokensUsed: openaiAnalysis.usage?.total_tokens || 0,
    model: openaiAnalysis.model,
    error: null
  };
}

// Create comprehensive document intelligence report
const documentIntelligence = {
  success: geminiDocumentResult.success,
  documentInfo: {
    type: config.documentType,
    processingType: config.processingType,
    language: config.language,
    requestId: config.requestId,
    processedAt: new Date().toISOString()
  },
  geminiAnalysis: {
    documentAnalysis: geminiDocumentResult.content,
    riskAssessment: geminiRiskResult.content,
    tokensUsed: geminiDocumentResult.tokensUsed + geminiRiskResult.tokensUsed,
    success: geminiDocumentResult.success && geminiRiskResult.success
  },
  openaiComparison: {
    analysis: openaiResult.content,
    tokensUsed: openaiResult.tokensUsed,
    success: openaiResult.success
  },
  performanceMetrics: {
    totalTokensGemini: geminiDocumentResult.tokensUsed + geminiRiskResult.tokensUsed,
    totalTokensOpenAI: openaiResult.tokensUsed,
    tokenEfficiency: {
      geminiVsOpenAI: ((geminiDocumentResult.tokensUsed + geminiRiskResult.tokensUsed) / openaiResult.tokensUsed).toFixed(2),
      winner: (geminiDocumentResult.tokensUsed + geminiRiskResult.tokensUsed) < openaiResult.tokensUsed ? 'Gemini' : 'OpenAI'
    },
    processingTime: (Date.now() - new Date(config.timestamp).getTime()) / 1000
  },
  qualityAssessment: {
    structuredOutput: geminiDocumentResult.success && geminiRiskResult.success,
    comprehensiveAnalysis: !!(geminiDocumentResult.content.document_summary && geminiDocumentResult.content.risks_and_concerns),
    riskAssessmentProvided: !!(geminiRiskResult.content.risk_level && geminiRiskResult.content.critical_risks),
    actionableRecommendations: !!(geminiDocumentResult.content.recommendations && geminiRiskResult.content.recommendations)
  },
  errors: {
    geminiDocument: geminiDocumentResult.error,
    geminiRisk: geminiRiskResult.error,
    openai: openaiResult.error
  }
};

return [{ json: documentIntelligence }];

// Format comprehensive document intelligence results for display
const intelligence = $input.first().json;

// Create executive summary
const executiveSummary = {
  documentType: intelligence.documentInfo.type,
  processingSuccess: intelligence.success,
  riskLevel: intelligence.geminiAnalysis.riskAssessment.risk_level || 'unknown',
  criticalRisks: intelligence.geminiAnalysis.riskAssessment.critical_risks?.length || 0,
  tokensEfficiency: intelligence.performanceMetrics.tokenEfficiency.winner,
  processingTime: `${intelligence.performanceMetrics.processingTime}s`,
  recommendedActions: intelligence.geminiAnalysis.riskAssessment.priority_actions?.length || 0
};

// Create detailed display output
const displayOutput = {
  'ðŸ“„ GEMINI PDF & DOCUMENT INTELLIGENCE REPORT': '=' .repeat(60),
  '': '',
  'ðŸ“Š Executive Summary': {
    'Document Type': intelligence.documentInfo.type.toUpperCase(),
    'Processing Status': intelligence.success ? 'âœ… Success' : 'âŒ Failed',
    'Risk Level': intelligence.geminiAnalysis.riskAssessment.risk_level?.toUpperCase() || 'Unknown',
    'Critical Risks Found': intelligence.geminiAnalysis.riskAssessment.critical_risks?.length || 0,
    'Processing Time': `${intelligence.performanceMetrics.processingTime} seconds`,
    'Token Efficiency Winner': intelligence.performanceMetrics.tokenEfficiency.winner
  },
  ' ': '',
  'ðŸŽ¯ GEMINI DOCUMENT ANALYSIS': {
    'Document Summary': intelligence.geminiAnalysis.documentAnalysis.document_summary || 'Not available',
    'Key Entities': intelligence.geminiAnalysis.documentAnalysis.key_entities || [],
    'Important Dates': intelligence.geminiAnalysis.documentAnalysis.important_dates || [],
    'Financial Terms': intelligence.geminiAnalysis.documentAnalysis.financial_terms || [],
    'Obligations & Rights': intelligence.geminiAnalysis.documentAnalysis.obligations_and_rights || [],
    'Tokens Used': intelligence.performanceMetrics.totalTokensGemini
  },
  '  ': '',
  'âš ï¸ GEMINI RISK ASSESSMENT': {
    'Overall Risk Level': intelligence.geminiAnalysis.riskAssessment.risk_level?.toUpperCase() || 'Unknown',
    'Critical Risks': intelligence.geminiAnalysis.riskAssessment.critical_risks || [],
    'Missing Clauses': intelligence.geminiAnalysis.riskAssessment.missing_clauses || [],
    'Compliance Issues': intelligence.geminiAnalysis.riskAssessment.compliance_issues || [],
    'Legal Review Required': intelligence.geminiAnalysis.riskAssessment.legal_review_required ? 'âœ… Yes' : 'âŒ No',
    'Priority Actions': intelligence.geminiAnalysis.riskAssessment.priority_actions || []
  },
  '   ': '',
  'ðŸ“ RECOMMENDATIONS': intelligence.geminiAnalysis.documentAnalysis.recommendations || [],
  '    ': '',
  'ðŸ”´ OPENAI COMPARISON': {
    'Analysis Status': intelligence.openaiComparison.success ? 'âœ… Success' : 'âŒ Failed',
    'Tokens Used': intelligence.performanceMetrics.totalTokensOpenAI,
    'Content Preview': intelligence.openaiComparison.analysis?.substring(0, 300) + '...' || 'No content'
  },
  '     ': '',
  'âš¡ PERFORMANCE METRICS': {
    'Gemini Total Tokens': intelligence.performanceMetrics.totalTokensGemini,
    'OpenAI Total Tokens': intelligence.performanceMetrics.totalTokensOpenAI,
    'Token Efficiency Ratio': intelligence.performanceMetrics.tokenEfficiency.geminiVsOpenAI,
    'Processing Time': `${intelligence.performanceMetrics.processingTime} seconds`,
    'Structured Output': intelligence.qualityAssessment.structuredOutput ? 'âœ… Yes' : 'âŒ No',
    'Comprehensive Analysis': intelligence.qualityAssessment.comprehensiveAnalysis ? 'âœ… Yes' : 'âŒ No'
  },
  '      ': '',
  'ðŸš€ GEMINI ADVANTAGES DEMONSTRATED': [
    intelligence.qualityAssessment.structuredOutput ? 'â€¢ Native JSON structured output' : 'â€¢ Standard text output',
    intelligence.geminiAnalysis.success ? 'â€¢ Comprehensive document analysis' : 'â€¢ Basic document processing',
    intelligence.geminiAnalysis.riskAssessment.risk_level ? 'â€¢ Advanced risk assessment capabilities' : 'â€¢ Standard analysis only',
    intelligence.performanceMetrics.tokenEfficiency.winner === 'Gemini' ? 'â€¢ Superior token efficiency' : 'â€¢ Standard token usage',
    'â€¢ Specialized PDF and document processing',
    'â€¢ Built-in safety and compliance filtering',
    'â€¢ Multi-step analysis in single workflow'
  ]
};

return [{
  json: {
    displayOutput: displayOutput,
    executiveSummary: executiveSummary,
    rawIntelligence: intelligence,
    summary: {
      status: intelligence.success ? 'completed' : 'failed',
      documentType: intelligence.documentInfo.type,
      riskLevel: intelligence.geminiAnalysis.riskAssessment.risk_level,
      criticalRisks: intelligence.geminiAnalysis.riskAssessment.critical_risks?.length || 0,
      recommendGemini: intelligence.success && intelligence.qualityAssessment.structuredOutput,
      processingTime: intelligence.performanceMetrics.processingTime
    }
  }
}];
