const weatherData = $input.all()[0].json;
const currentWeather = weatherData.current_condition[0];
const todayWeather = weatherData.weather[0];
const currentTime = new Date();
const formattedDate = currentTime.toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});
const formattedTime = currentTime.toLocaleTimeString('en-US', {
  hour: '2-digit',
  minute: '2-digit',
  timeZoneName: 'short'
});

const temperature = `${currentWeather.temp_F}¬∞F (${currentWeather.temp_C}¬∞C)`;
const condition = currentWeather.weatherDesc[0].value;
const humidity = `${currentWeather.humidity}%`;
const windSpeed = `${currentWeather.windspeedMiles} mph`;
const windDir = currentWeather.winddir16Point;
const feelsLike = `${currentWeather.FeelsLikeF}¬∞F`;
const uvIndex = currentWeather.uvIndex;
const visibility = `${currentWeather.visibility} miles`;
const pressure = `${currentWeather.pressure} mb`;

const maxTemp = `${todayWeather.maxtempF}¬∞F`;
const minTemp = `${todayWeather.mintempF}¬∞F`;
const sunrise = todayWeather.astronomy[0].sunrise;
const sunset = todayWeather.astronomy[0].sunset;

const emailSubject = `üå§Ô∏è Boston Weather Update - ${formattedDate}`;

const emailHtml = `
<!DOCTYPE html>
