// Simple math calculation from webhook data
const data = $input.first().json;
const { a, b, operation } = data;

let result;
switch(operation) {
  case 'add':
    result = a + b;
    break;
  case 'subtract':
    result = a - b;
    break;
  case 'multiply':
    result = a * b;
    break;
  case 'divide':
    result = b !== 0 ? a / b : 'Error: Division by zero';
    break;
  default:
    result = 'Error: Invalid operation';
}

return [{
  json: {
    input: { a, b, operation },
    result: result,
    timestamp: new Date().toISOString(),
    message: `${a} ${operation} ${b} = ${result}`
  }
}];
